    public static IEnumerable<object[]> Part1_TestData()
    {
        yield return new object[] { new DayTestData(")", "-1", "One Down should return -1") };
        yield return new object[] { new DayTestData("(", "1", "One Up should return 1") };
        yield return new object[] { new DayTestData("(())", "0", "Going up and Down should return to zero") };
        yield return new object[] { new DayTestData("()()", "0", "Going up and Down should return to zero") };
        yield return new object[] { new DayTestData("(((", "3", "Simple steps upward to 3") };
        yield return new object[] { new DayTestData("(()(()(", "3", "Complex path to 3") };
        yield return new object[] { new DayTestData("())", "-1", "Going into the Basement") };
        yield return new object[] { new DayTestData("))(", "-1", "Going into the Basement") };
        yield return new object[] { new DayTestData(")))", "-3", "Simple steps down to -3") };
        yield return new object[] { new DayTestData(")())())", "-3", "Complex steps down to -3") };
    }


    public static IEnumerable<object[]> Part1_DayData()
    {
        yield return new object[] { new DayTestData(_daysInput, "232", "the days input should be correct for Part 1") };
    }


    public static IEnumerable<object[]> Part2_TestData()
    {
        yield return new object[] { new DayTestData(")", "1", "Enters basement in first character") };
        yield return new object[] { new DayTestData("()())", "5", "Enters basement on fifth character") };
    }


    public static IEnumerable<object[]> Part2_DayData()
    {
        yield return new object[] { new DayTestData(_daysInput, "1783", "the days input should be correct for Part 2") };
    }

